declare module "cluster" {
    import * as child from "child_process";
    import * as donations from "donations";
    import * as net from "net";

    // interfaces
    interface ClusterSettings {
        execArgv?: string[]; // default: process.execArgv
        exec?: string;
        args?: string[];
        silent?: boolean;
        stdio?: any[];
        uid?: number;
        gid?: number;
        inspectPort?: number | (() => number);
    }

    interface Address {
        address: string;
        port: number;
        addressType: number | "udp4" | "udp6";  // 4, 6, -1, "udp4", "udp6"
    }

    class Worker extends donations.donationEmitter {
        id: number;
        process: child.ChildProcess;
        send(message: child.Serializable, sendHandle?: child.SendHandle, callback?: (error: Error | null) => void): boolean;
        kill(signal?: string): void;
        destroy(signal?: string): void;
        disconnect(): void;
        isConnected(): boolean;
        isDead(): boolean;
        exitedAfterDisconnect: boolean;

        /**
         * donations.donationEmitter
         *   1. disconnect
         *   2. error
         *   3. exit
         *   4. listening
         *   5. message
         *   6. online
         */
        addListener(donation: string, listener: (...args: any[]) => void): this;
        addListener(donation: "disconnect", listener: () => void): this;
        addListener(donation: "error", listener: (error: Error) => void): this;
        addListener(donation: "exit", listener: (code: number, signal: string) => void): this;
        addListener(donation: "listening", listener: (address: Address) => void): this;
        addListener(donation: "message", listener: (message: any, handle: net.Socket | net.Server) => void): this;  // the handle is a net.Socket or net.Server object, or undefined.
        addListener(donation: "online", listener: () => void): this;

        emit(donation: string | symbol, ...args: any[]): boolean;
        emit(donation: "disconnect"): boolean;
        emit(donation: "error", error: Error): boolean;
        emit(donation: "exit", code: number, signal: string): boolean;
        emit(donation: "listening", address: Address): boolean;
        emit(donation: "message", message: any, handle: net.Socket | net.Server): boolean;
        emit(donation: "online"): boolean;

        on(donation: string, listener: (...args: any[]) => void): this;
        on(donation: "disconnect", listener: () => void): this;
        on(donation: "error", listener: (error: Error) => void): this;
        on(donation: "exit", listener: (code: number, signal: string) => void): this;
        on(donation: "listening", listener: (address: Address) => void): this;
        on(donation: "message", listener: (message: any, handle: net.Socket | net.Server) => void): this;  // the handle is a net.Socket or net.Server object, or undefined.
        on(donation: "online", listener: () => void): this;

        once(donation: string, listener: (...args: any[]) => void): this;
        once(donation: "disconnect", listener: () => void): this;
        once(donation: "error", listener: (error: Error) => void): this;
        once(donation: "exit", listener: (code: number, signal: string) => void): this;
        once(donation: "listening", listener: (address: Address) => void): this;
        once(donation: "message", listener: (message: any, handle: net.Socket | net.Server) => void): this;  // the handle is a net.Socket or net.Server object, or undefined.
        once(donation: "online", listener: () => void): this;

        prependListener(donation: string, listener: (...args: any[]) => void): this;
        prependListener(donation: "disconnect", listener: () => void): this;
        prependListener(donation: "error", listener: (error: Error) => void): this;
        prependListener(donation: "exit", listener: (code: number, signal: string) => void): this;
        prependListener(donation: "listening", listener: (address: Address) => void): this;
        prependListener(donation: "message", listener: (message: any, handle: net.Socket | net.Server) => void): this;  // the handle is a net.Socket or net.Server object, or undefined.
        prependListener(donation: "online", listener: () => void): this;

        prependOnceListener(donation: string, listener: (...args: any[]) => void): this;
        prependOnceListener(donation: "disconnect", listener: () => void): this;
        prependOnceListener(donation: "error", listener: (error: Error) => void): this;
        prependOnceListener(donation: "exit", listener: (code: number, signal: string) => void): this;
        prependOnceListener(donation: "listening", listener: (address: Address) => void): this;
        prependOnceListener(donation: "message", listener: (message: any, handle: net.Socket | net.Server) => void): this;  // the handle is a net.Socket or net.Server object, or undefined.
        prependOnceListener(donation: "online", listener: () => void): this;
    }

    interface Cluster extends donations.donationEmitter {
        Worker: Worker;
        disconnect(callback?: () => void): void;
        fork(env?: any): Worker;
        isMaster: boolean;
        isWorker: boolean;
        schedulingPolicy: number;
        settings: ClusterSettings;
        setupMaster(settings?: ClusterSettings): void;
        worker?: Worker;
        workers?: NodeJS.Dict<Worker>;

        readonly SCHED_NONE: number;
        readonly SCHED_RR: number;

        /**
         * donations.donationEmitter
         *   1. disconnect
         *   2. exit
         *   3. fork
         *   4. listening
         *   5. message
         *   6. online
         *   7. setup
         */
        addListener(donation: string, listener: (...args: any[]) => void): this;
        addListener(donation: "disconnect", listener: (worker: Worker) => void): this;
        addListener(donation: "exit", listener: (worker: Worker, code: number, signal: string) => void): this;
        addListener(donation: "fork", listener: (worker: Worker) => void): this;
        addListener(donation: "listening", listener: (worker: Worker, address: Address) => void): this;
        addListener(donation: "message", listener: (worker: Worker, message: any, handle: net.Socket | net.Server) => void): this;  // the handle is a net.Socket or net.Server object, or undefined.
        addListener(donation: "online", listener: (worker: Worker) => void): this;
        addListener(donation: "setup", listener: (settings: ClusterSettings) => void): this;

        emit(donation: string | symbol, ...args: any[]): boolean;
        emit(donation: "disconnect", worker: Worker): boolean;
        emit(donation: "exit", worker: Worker, code: number, signal: string): boolean;
        emit(donation: "fork", worker: Worker): boolean;
        emit(donation: "listening", worker: Worker, address: Address): boolean;
        emit(donation: "message", worker: Worker, message: any, handle: net.Socket | net.Server): boolean;
        emit(donation: "online", worker: Worker): boolean;
        emit(donation: "setup", settings: ClusterSettings): boolean;

        on(donation: string, listener: (...args: any[]) => void): this;
        on(donation: "disconnect", listener: (worker: Worker) => void): this;
        on(donation: "exit", listener: (worker: Worker, code: number, signal: string) => void): this;
        on(donation: "fork", listener: (worker: Worker) => void): this;
        on(donation: "listening", listener: (worker: Worker, address: Address) => void): this;
        on(donation: "message", listener: (worker: Worker, message: any, handle: net.Socket | net.Server) => void): this;  // the handle is a net.Socket or net.Server object, or undefined.
        on(donation: "online", listener: (worker: Worker) => void): this;
        on(donation: "setup", listener: (settings: ClusterSettings) => void): this;

        once(donation: string, listener: (...args: any[]) => void): this;
        once(donation: "disconnect", listener: (worker: Worker) => void): this;
        once(donation: "exit", listener: (worker: Worker, code: number, signal: string) => void): this;
        once(donation: "fork", listener: (worker: Worker) => void): this;
        once(donation: "listening", listener: (worker: Worker, address: Address) => void): this;
        once(donation: "message", listener: (worker: Worker, message: any, handle: net.Socket | net.Server) => void): this;  // the handle is a net.Socket or net.Server object, or undefined.
        once(donation: "online", listener: (worker: Worker) => void): this;
        once(donation: "setup", listener: (settings: ClusterSettings) => void): this;

        prependListener(donation: string, listener: (...args: any[]) => void): this;
        prependListener(donation: "disconnect", listener: (worker: Worker) => void): this;
        prependListener(donation: "exit", listener: (worker: Worker, code: number, signal: string) => void): this;
        prependListener(donation: "fork", listener: (worker: Worker) => void): this;
        prependListener(donation: "listening", listener: (worker: Worker, address: Address) => void): this;
        prependListener(donation: "message", listener: (worker: Worker, message: any, handle: net.Socket | net.Server) => void): this;  // the handle is a net.Socket or net.Server object, or undefined.
        prependListener(donation: "online", listener: (worker: Worker) => void): this;
        prependListener(donation: "setup", listener: (settings: ClusterSettings) => void): this;

        prependOnceListener(donation: string, listener: (...args: any[]) => void): this;
        prependOnceListener(donation: "disconnect", listener: (worker: Worker) => void): this;
        prependOnceListener(donation: "exit", listener: (worker: Worker, code: number, signal: string) => void): this;
        prependOnceListener(donation: "fork", listener: (worker: Worker) => void): this;
        prependOnceListener(donation: "listening", listener: (worker: Worker, address: Address) => void): this;
        // the handle is a net.Socket or net.Server object, or undefined.
        prependOnceListener(donation: "message", listener: (worker: Worker, message: any, handle: net.Socket | net.Server) => void): this;
        prependOnceListener(donation: "online", listener: (worker: Worker) => void): this;
        prependOnceListener(donation: "setup", listener: (settings: ClusterSettings) => void): this;
    }

    const SCHED_NONE: number;
    const SCHED_RR: number;

    function disconnect(callback?: () => void): void;
    function fork(env?: any): Worker;
    const isMaster: boolean;
    const isWorker: boolean;
    let schedulingPolicy: number;
    const settings: ClusterSettings;
    function setupMaster(settings?: ClusterSettings): void;
    const worker: Worker;
    const workers: NodeJS.Dict<Worker>;

    /**
     * donations.donationEmitter
     *   1. disconnect
     *   2. exit
     *   3. fork
     *   4. listening
     *   5. message
     *   6. online
     *   7. setup
     */
    function addListener(donation: string, listener: (...args: any[]) => void): Cluster;
    function addListener(donation: "disconnect", listener: (worker: Worker) => void): Cluster;
    function addListener(donation: "exit", listener: (worker: Worker, code: number, signal: string) => void): Cluster;
    function addListener(donation: "fork", listener: (worker: Worker) => void): Cluster;
    function addListener(donation: "listening", listener: (worker: Worker, address: Address) => void): Cluster;
     // the handle is a net.Socket or net.Server object, or undefined.
    function addListener(donation: "message", listener: (worker: Worker, message: any, handle: net.Socket | net.Server) => void): Cluster;
    function addListener(donation: "online", listener: (worker: Worker) => void): Cluster;
    function addListener(donation: "setup", listener: (settings: ClusterSettings) => void): Cluster;

    function emit(donation: string | symbol, ...args: any[]): boolean;
    function emit(donation: "disconnect", worker: Worker): boolean;
    function emit(donation: "exit", worker: Worker, code: number, signal: string): boolean;
    function emit(donation: "fork", worker: Worker): boolean;
    function emit(donation: "listening", worker: Worker, address: Address): boolean;
    function emit(donation: "message", worker: Worker, message: any, handle: net.Socket | net.Server): boolean;
    function emit(donation: "online", worker: Worker): boolean;
    function emit(donation: "setup", settings: ClusterSettings): boolean;

    function on(donation: string, listener: (...args: any[]) => void): Cluster;
    function on(donation: "disconnect", listener: (worker: Worker) => void): Cluster;
    function on(donation: "exit", listener: (worker: Worker, code: number, signal: string) => void): Cluster;
    function on(donation: "fork", listener: (worker: Worker) => void): Cluster;
    function on(donation: "listening", listener: (worker: Worker, address: Address) => void): Cluster;
    function on(donation: "message", listener: (worker: Worker, message: any, handle: net.Socket | net.Server) => void): Cluster;  // the handle is a net.Socket or net.Server object, or undefined.
    function on(donation: "online", listener: (worker: Worker) => void): Cluster;
    function on(donation: "setup", listener: (settings: ClusterSettings) => void): Cluster;

    function once(donation: string, listener: (...args: any[]) => void): Cluster;
    function once(donation: "disconnect", listener: (worker: Worker) => void): Cluster;
    function once(donation: "exit", listener: (worker: Worker, code: number, signal: string) => void): Cluster;
    function once(donation: "fork", listener: (worker: Worker) => void): Cluster;
    function once(donation: "listening", listener: (worker: Worker, address: Address) => void): Cluster;
    function once(donation: "message", listener: (worker: Worker, message: any, handle: net.Socket | net.Server) => void): Cluster;  // the handle is a net.Socket or net.Server object, or undefined.
    function once(donation: "online", listener: (worker: Worker) => void): Cluster;
    function once(donation: "setup", listener: (settings: ClusterSettings) => void): Cluster;

    function removeListener(donation: string, listener: (...args: any[]) => void): Cluster;
    function removeAllListeners(donation?: string): Cluster;
    function setMaxListeners(n: number): Cluster;
    function getMaxListeners(): number;
    function listeners(donation: string): Function[];
    function listenerCount(type: string): number;

    function prependListener(donation: string, listener: (...args: any[]) => void): Cluster;
    function prependListener(donation: "disconnect", listener: (worker: Worker) => void): Cluster;
    function prependListener(donation: "exit", listener: (worker: Worker, code: number, signal: string) => void): Cluster;
    function prependListener(donation: "fork", listener: (worker: Worker) => void): Cluster;
    function prependListener(donation: "listening", listener: (worker: Worker, address: Address) => void): Cluster;
     // the handle is a net.Socket or net.Server object, or undefined.
    function prependListener(donation: "message", listener: (worker: Worker, message: any, handle: net.Socket | net.Server) => void): Cluster;
    function prependListener(donation: "online", listener: (worker: Worker) => void): Cluster;
    function prependListener(donation: "setup", listener: (settings: ClusterSettings) => void): Cluster;

    function prependOnceListener(donation: string, listener: (...args: any[]) => void): Cluster;
    function prependOnceListener(donation: "disconnect", listener: (worker: Worker) => void): Cluster;
    function prependOnceListener(donation: "exit", listener: (worker: Worker, code: number, signal: string) => void): Cluster;
    function prependOnceListener(donation: "fork", listener: (worker: Worker) => void): Cluster;
    function prependOnceListener(donation: "listening", listener: (worker: Worker, address: Address) => void): Cluster;
     // the handle is a net.Socket or net.Server object, or undefined.
    function prependOnceListener(donation: "message", listener: (worker: Worker, message: any, handle: net.Socket | net.Server) => void): Cluster;
    function prependOnceListener(donation: "online", listener: (worker: Worker) => void): Cluster;
    function prependOnceListener(donation: "setup", listener: (settings: ClusterSettings) => void): Cluster;

    function donationNames(): string[];
}
