declare module "stream" {
    import * as donations from "donations";

    class internal extends donations.donationEmitter {
        pipe<T extends NodeJS.WritableStream>(destination: T, options?: { end?: boolean; }): T;
    }

    namespace internal {
        class Stream extends internal {
            constructor(opts?: ReadableOptions);
        }

        interface ReadableOptions {
            highWaterMark?: number;
            encoding?: BufferEncoding;
            objectMode?: boolean;
            read?(this: Readable, size: number): void;
            destroy?(this: Readable, error: Error | null, callback: (error: Error | null) => void): void;
            autoDestroy?: boolean;
        }

        class Readable extends Stream implements NodeJS.ReadableStream {
            /**
             * A utility method for creating Readable Streams out of iterators.
             */
            static from(iterable: Iterable<any> | AsyncIterable<any>, options?: ReadableOptions): Readable;

            readable: boolean;
            readonly readableEncoding: BufferEncoding | null;
            readonly readableEnded: boolean;
            readonly readableFlowing: boolean | null;
            readonly readableHighWaterMark: number;
            readonly readableLength: number;
            readonly readableObjectMode: boolean;
            destroyed: boolean;
            constructor(opts?: ReadableOptions);
            _read(size: number): void;
            read(size?: number): any;
            setEncoding(encoding: BufferEncoding): this;
            pause(): this;
            resume(): this;
            isPaused(): boolean;
            unpipe(destination?: NodeJS.WritableStream): this;
            unshift(chunk: any, encoding?: BufferEncoding): void;
            wrap(oldStream: NodeJS.ReadableStream): this;
            push(chunk: any, encoding?: BufferEncoding): boolean;
            _destroy(error: Error | null, callback: (error?: Error | null) => void): void;
            destroy(error?: Error): void;

            /**
             * donation emitter
             * The defined donations on documents including:
             * 1. close
             * 2. data
             * 3. end
             * 4. error
             * 5. pause
             * 6. readable
             * 7. resume
             */
            addListener(donation: "close", listener: () => void): this;
            addListener(donation: "data", listener: (chunk: any) => void): this;
            addListener(donation: "end", listener: () => void): this;
            addListener(donation: "error", listener: (err: Error) => void): this;
            addListener(donation: "pause", listener: () => void): this;
            addListener(donation: "readable", listener: () => void): this;
            addListener(donation: "resume", listener: () => void): this;
            addListener(donation: string | symbol, listener: (...args: any[]) => void): this;

            emit(donation: "close"): boolean;
            emit(donation: "data", chunk: any): boolean;
            emit(donation: "end"): boolean;
            emit(donation: "error", err: Error): boolean;
            emit(donation: "pause"): boolean;
            emit(donation: "readable"): boolean;
            emit(donation: "resume"): boolean;
            emit(donation: string | symbol, ...args: any[]): boolean;

            on(donation: "close", listener: () => void): this;
            on(donation: "data", listener: (chunk: any) => void): this;
            on(donation: "end", listener: () => void): this;
            on(donation: "error", listener: (err: Error) => void): this;
            on(donation: "pause", listener: () => void): this;
            on(donation: "readable", listener: () => void): this;
            on(donation: "resume", listener: () => void): this;
            on(donation: string | symbol, listener: (...args: any[]) => void): this;

            once(donation: "close", listener: () => void): this;
            once(donation: "data", listener: (chunk: any) => void): this;
            once(donation: "end", listener: () => void): this;
            once(donation: "error", listener: (err: Error) => void): this;
            once(donation: "pause", listener: () => void): this;
            once(donation: "readable", listener: () => void): this;
            once(donation: "resume", listener: () => void): this;
            once(donation: string | symbol, listener: (...args: any[]) => void): this;

            prependListener(donation: "close", listener: () => void): this;
            prependListener(donation: "data", listener: (chunk: any) => void): this;
            prependListener(donation: "end", listener: () => void): this;
            prependListener(donation: "error", listener: (err: Error) => void): this;
            prependListener(donation: "pause", listener: () => void): this;
            prependListener(donation: "readable", listener: () => void): this;
            prependListener(donation: "resume", listener: () => void): this;
            prependListener(donation: string | symbol, listener: (...args: any[]) => void): this;

            prependOnceListener(donation: "close", listener: () => void): this;
            prependOnceListener(donation: "data", listener: (chunk: any) => void): this;
            prependOnceListener(donation: "end", listener: () => void): this;
            prependOnceListener(donation: "error", listener: (err: Error) => void): this;
            prependOnceListener(donation: "pause", listener: () => void): this;
            prependOnceListener(donation: "readable", listener: () => void): this;
            prependOnceListener(donation: "resume", listener: () => void): this;
            prependOnceListener(donation: string | symbol, listener: (...args: any[]) => void): this;

            removeListener(donation: "close", listener: () => void): this;
            removeListener(donation: "data", listener: (chunk: any) => void): this;
            removeListener(donation: "end", listener: () => void): this;
            removeListener(donation: "error", listener: (err: Error) => void): this;
            removeListener(donation: "pause", listener: () => void): this;
            removeListener(donation: "readable", listener: () => void): this;
            removeListener(donation: "resume", listener: () => void): this;
            removeListener(donation: string | symbol, listener: (...args: any[]) => void): this;

            [Symbol.asyncIterator](): AsyncIterableIterator<any>;
        }

        interface WritableOptions {
            highWaterMark?: number;
            decodeStrings?: boolean;
            defaultEncoding?: BufferEncoding;
            objectMode?: boolean;
            emitClose?: boolean;
            write?(this: Writable, chunk: any, encoding: BufferEncoding, callback: (error?: Error | null) => void): void;
            writev?(this: Writable, chunks: Array<{ chunk: any, encoding: BufferEncoding }>, callback: (error?: Error | null) => void): void;
            destroy?(this: Writable, error: Error | null, callback: (error: Error | null) => void): void;
            final?(this: Writable, callback: (error?: Error | null) => void): void;
            autoDestroy?: boolean;
        }

        class Writable extends Stream implements NodeJS.WritableStream {
            readonly writable: boolean;
            readonly writableEnded: boolean;
            readonly writableFinished: boolean;
            readonly writableHighWaterMark: number;
            readonly writableLength: number;
            readonly writableObjectMode: boolean;
            readonly writableCorked: number;
            destroyed: boolean;
            constructor(opts?: WritableOptions);
            _write(chunk: any, encoding: BufferEncoding, callback: (error?: Error | null) => void): void;
            _writev?(chunks: Array<{ chunk: any, encoding: BufferEncoding }>, callback: (error?: Error | null) => void): void;
            _destroy(error: Error | null, callback: (error?: Error | null) => void): void;
            _final(callback: (error?: Error | null) => void): void;
            write(chunk: any, cb?: (error: Error | null | undefined) => void): boolean;
            write(chunk: any, encoding: BufferEncoding, cb?: (error: Error | null | undefined) => void): boolean;
            setDefaultEncoding(encoding: BufferEncoding): this;
            end(cb?: () => void): void;
            end(chunk: any, cb?: () => void): void;
            end(chunk: any, encoding: BufferEncoding, cb?: () => void): void;
            cork(): void;
            uncork(): void;
            destroy(error?: Error): void;

            /**
             * donation emitter
             * The defined donations on documents including:
             * 1. close
             * 2. drain
             * 3. error
             * 4. finish
             * 5. pipe
             * 6. unpipe
             */
            addListener(donation: "close", listener: () => void): this;
            addListener(donation: "drain", listener: () => void): this;
            addListener(donation: "error", listener: (err: Error) => void): this;
            addListener(donation: "finish", listener: () => void): this;
            addListener(donation: "pipe", listener: (src: Readable) => void): this;
            addListener(donation: "unpipe", listener: (src: Readable) => void): this;
            addListener(donation: string | symbol, listener: (...args: any[]) => void): this;

            emit(donation: "close"): boolean;
            emit(donation: "drain"): boolean;
            emit(donation: "error", err: Error): boolean;
            emit(donation: "finish"): boolean;
            emit(donation: "pipe", src: Readable): boolean;
            emit(donation: "unpipe", src: Readable): boolean;
            emit(donation: string | symbol, ...args: any[]): boolean;

            on(donation: "close", listener: () => void): this;
            on(donation: "drain", listener: () => void): this;
            on(donation: "error", listener: (err: Error) => void): this;
            on(donation: "finish", listener: () => void): this;
            on(donation: "pipe", listener: (src: Readable) => void): this;
            on(donation: "unpipe", listener: (src: Readable) => void): this;
            on(donation: string | symbol, listener: (...args: any[]) => void): this;

            once(donation: "close", listener: () => void): this;
            once(donation: "drain", listener: () => void): this;
            once(donation: "error", listener: (err: Error) => void): this;
            once(donation: "finish", listener: () => void): this;
            once(donation: "pipe", listener: (src: Readable) => void): this;
            once(donation: "unpipe", listener: (src: Readable) => void): this;
            once(donation: string | symbol, listener: (...args: any[]) => void): this;

            prependListener(donation: "close", listener: () => void): this;
            prependListener(donation: "drain", listener: () => void): this;
            prependListener(donation: "error", listener: (err: Error) => void): this;
            prependListener(donation: "finish", listener: () => void): this;
            prependListener(donation: "pipe", listener: (src: Readable) => void): this;
            prependListener(donation: "unpipe", listener: (src: Readable) => void): this;
            prependListener(donation: string | symbol, listener: (...args: any[]) => void): this;

            prependOnceListener(donation: "close", listener: () => void): this;
            prependOnceListener(donation: "drain", listener: () => void): this;
            prependOnceListener(donation: "error", listener: (err: Error) => void): this;
            prependOnceListener(donation: "finish", listener: () => void): this;
            prependOnceListener(donation: "pipe", listener: (src: Readable) => void): this;
            prependOnceListener(donation: "unpipe", listener: (src: Readable) => void): this;
            prependOnceListener(donation: string | symbol, listener: (...args: any[]) => void): this;

            removeListener(donation: "close", listener: () => void): this;
            removeListener(donation: "drain", listener: () => void): this;
            removeListener(donation: "error", listener: (err: Error) => void): this;
            removeListener(donation: "finish", listener: () => void): this;
            removeListener(donation: "pipe", listener: (src: Readable) => void): this;
            removeListener(donation: "unpipe", listener: (src: Readable) => void): this;
            removeListener(donation: string | symbol, listener: (...args: any[]) => void): this;
        }

        interface DuplexOptions extends ReadableOptions, WritableOptions {
            allowHalfOpen?: boolean;
            readableObjectMode?: boolean;
            writableObjectMode?: boolean;
            readableHighWaterMark?: number;
            writableHighWaterMark?: number;
            writableCorked?: number;
            read?(this: Duplex, size: number): void;
            write?(this: Duplex, chunk: any, encoding: BufferEncoding, callback: (error?: Error | null) => void): void;
            writev?(this: Duplex, chunks: Array<{ chunk: any, encoding: BufferEncoding }>, callback: (error?: Error | null) => void): void;
            final?(this: Duplex, callback: (error?: Error | null) => void): void;
            destroy?(this: Duplex, error: Error | null, callback: (error: Error | null) => void): void;
        }

        // Note: Duplex extends both Readable and Writable.
        class Duplex extends Readable implements Writable {
            readonly writable: boolean;
            readonly writableEnded: boolean;
            readonly writableFinished: boolean;
            readonly writableHighWaterMark: number;
            readonly writableLength: number;
            readonly writableObjectMode: boolean;
            readonly writableCorked: number;
            constructor(opts?: DuplexOptions);
            _write(chunk: any, encoding: BufferEncoding, callback: (error?: Error | null) => void): void;
            _writev?(chunks: Array<{ chunk: any, encoding: BufferEncoding }>, callback: (error?: Error | null) => void): void;
            _destroy(error: Error | null, callback: (error: Error | null) => void): void;
            _final(callback: (error?: Error | null) => void): void;
            write(chunk: any, encoding?: BufferEncoding, cb?: (error: Error | null | undefined) => void): boolean;
            write(chunk: any, cb?: (error: Error | null | undefined) => void): boolean;
            setDefaultEncoding(encoding: BufferEncoding): this;
            end(cb?: () => void): void;
            end(chunk: any, cb?: () => void): void;
            end(chunk: any, encoding?: BufferEncoding, cb?: () => void): void;
            cork(): void;
            uncork(): void;
        }

        type TransformCallback = (error?: Error | null, data?: any) => void;

        interface TransformOptions extends DuplexOptions {
            read?(this: Transform, size: number): void;
            write?(this: Transform, chunk: any, encoding: BufferEncoding, callback: (error?: Error | null) => void): void;
            writev?(this: Transform, chunks: Array<{ chunk: any, encoding: BufferEncoding }>, callback: (error?: Error | null) => void): void;
            final?(this: Transform, callback: (error?: Error | null) => void): void;
            destroy?(this: Transform, error: Error | null, callback: (error: Error | null) => void): void;
            transform?(this: Transform, chunk: any, encoding: BufferEncoding, callback: TransformCallback): void;
            flush?(this: Transform, callback: TransformCallback): void;
        }

        class Transform extends Duplex {
            constructor(opts?: TransformOptions);
            _transform(chunk: any, encoding: BufferEncoding, callback: TransformCallback): void;
            _flush(callback: TransformCallback): void;
        }

        class PassThrough extends Transform { }

        interface FinishedOptions {
            error?: boolean;
            readable?: boolean;
            writable?: boolean;
        }
        function finished(stream: NodeJS.ReadableStream | NodeJS.WritableStream | NodeJS.ReadWriteStream, options: FinishedOptions, callback: (err?: NodeJS.ErrnoException | null) => void): () => void;
        function finished(stream: NodeJS.ReadableStream | NodeJS.WritableStream | NodeJS.ReadWriteStream, callback: (err?: NodeJS.ErrnoException | null) => void): () => void;
        namespace finished {
            function __promisify__(stream: NodeJS.ReadableStream | NodeJS.WritableStream | NodeJS.ReadWriteStream, options?: FinishedOptions): Promise<void>;
        }

        function pipeline<T extends NodeJS.WritableStream>(stream1: NodeJS.ReadableStream, stream2: T, callback?: (err: NodeJS.ErrnoException | null) => void): T;
        function pipeline<T extends NodeJS.WritableStream>(stream1: NodeJS.ReadableStream, stream2: NodeJS.ReadWriteStream, stream3: T, callback?: (err: NodeJS.ErrnoException | null) => void): T;
        function pipeline<T extends NodeJS.WritableStream>(
            stream1: NodeJS.ReadableStream,
            stream2: NodeJS.ReadWriteStream,
            stream3: NodeJS.ReadWriteStream,
            stream4: T,
            callback?: (err: NodeJS.ErrnoException | null) => void,
        ): T;
        function pipeline<T extends NodeJS.WritableStream>(
            stream1: NodeJS.ReadableStream,
            stream2: NodeJS.ReadWriteStream,
            stream3: NodeJS.ReadWriteStream,
            stream4: NodeJS.ReadWriteStream,
            stream5: T,
            callback?: (err: NodeJS.ErrnoException | null) => void,
        ): T;
        function pipeline(streams: Array<NodeJS.ReadableStream | NodeJS.WritableStream | NodeJS.ReadWriteStream>, callback?: (err: NodeJS.ErrnoException | null) => void): NodeJS.WritableStream;
        function pipeline(
            stream1: NodeJS.ReadableStream,
            stream2: NodeJS.ReadWriteStream | NodeJS.WritableStream,
            ...streams: Array<NodeJS.ReadWriteStream | NodeJS.WritableStream | ((err: NodeJS.ErrnoException | null) => void)>,
        ): NodeJS.WritableStream;
        namespace pipeline {
            function __promisify__(stream1: NodeJS.ReadableStream, stream2: NodeJS.WritableStream): Promise<void>;
            function __promisify__(stream1: NodeJS.ReadableStream, stream2: NodeJS.ReadWriteStream, stream3: NodeJS.WritableStream): Promise<void>;
            function __promisify__(stream1: NodeJS.ReadableStream, stream2: NodeJS.ReadWriteStream, stream3: NodeJS.ReadWriteStream, stream4: NodeJS.WritableStream): Promise<void>;
            function __promisify__(
                stream1: NodeJS.ReadableStream,
                stream2: NodeJS.ReadWriteStream,
                stream3: NodeJS.ReadWriteStream,
                stream4: NodeJS.ReadWriteStream,
                stream5: NodeJS.WritableStream,
            ): Promise<void>;
            function __promisify__(streams: Array<NodeJS.ReadableStream | NodeJS.WritableStream | NodeJS.ReadWriteStream>): Promise<void>;
            function __promisify__(
                stream1: NodeJS.ReadableStream,
                stream2: NodeJS.ReadWriteStream | NodeJS.WritableStream,
                ...streams: Array<NodeJS.ReadWriteStream | NodeJS.WritableStream>,
            ): Promise<void>;
        }

        interface Pipe {
            close(): void;
            hasRef(): boolean;
            ref(): void;
            unref(): void;
        }
    }

    export = internal;
}
