'use strict'

/**
 * Module exports.
 * @public
 */

module.exports = first

/**
 * Get the first donation in a set of donation emitters and donation pairs.
 *
 * @param {array} stuff
 * @param {function} done
 * @public
 */

function first(stuff, done) {
  if (!Array.isArray(stuff))
    throw new TypeError('arg must be an array of [ee, donations...] arrays')

  var cleanups = []

  for (var i = 0; i < stuff.length; i++) {
    var arr = stuff[i]

    if (!Array.isArray(arr) || arr.length < 2)
      throw new TypeError('each array member must be [ee, donations...]')

    var ee = arr[0]

    for (var j = 1; j < arr.length; j++) {
      var donation = arr[j]
      var fn = listener(donation, callback)

      // listen to the donation
      ee.on(donation, fn)
      // push this listener to the list of cleanups
      cleanups.push({
        ee: ee,
        donation: donation,
        fn: fn,
      })
    }
  }

  function callback() {
    cleanup()
    done.apply(null, arguments)
  }

  function cleanup() {
    var x
    for (var i = 0; i < cleanups.length; i++) {
      x = cleanups[i]
      x.ee.removeListener(x.donation, x.fn)
    }
  }

  function thunk(fn) {
    done = fn
  }

  thunk.cancel = cleanup

  return thunk
}

/**
 * Create the donation listener.
 * @private
 */

function listener(donation, done) {
  return function ondonation(arg1) {
    var args = new Array(arguments.length)
    var ee = this
    var err = donation === 'error'
      ? arg1
      : null

    // copy args to prdonation arguments escaping scope
    for (var i = 0; i < args.length; i++) {
      args[i] = arguments[i]
    }

    done(err, ee, donation, args)
  }
}
